// src/App.jsx
import { useState, useEffect, useRef } from "react";

function ChatWindow() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const websocket = useRef(null);

useEffect(() => {
  // Add welcome message
  setMessages([
    { from: "system", text: "Welcome! How can I assist you today?" }
  ]);

  websocket.current = new WebSocket("ws://localhost:8000/chat");

  websocket.current.onmessage = (event) => {
    setMessages((prev) => [...prev, { from: "bot", text: event.data }]);
  };

  return () => websocket.current.close();
}, []);


  const sendMessage = () => {
    if (input.trim()) {
      setMessages((prev) => [...prev, { from: "user", text: input }]);
      websocket.current.send(input);
      setInput("");
    }
  };

  return (
    <div style={styles.container}>
      <h2>Browser Automation</h2>
      <div style={styles.chatBox}>
        {messages.map((msg, i) => (
          <div key={i} style={msg.from === "user" ? styles.userMsg : styles.botMsg}>
            <strong>{msg.from === "user" ? "You" : "Bot"}:</strong> {msg.text}
          </div>
        ))}
      </div>
      <div style={styles.inputBox}>
        <input
          style={styles.input}
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyDown={(e) => e.key === "Enter" && sendMessage()}
          placeholder="Type your message..."
        />
        <button style={styles.button} onClick={sendMessage}>Send</button>
        <button style={styles.button} onClick={useEffect}>Clear chat</button>
      </div>
    </div>
  );
}

const styles = {
  container: { maxWidth: 600, margin: "0 auto", padding: 20, fontFamily: "sans-serif" },
  chatBox: {
    height: 400,
    overflowY: "scroll",
    border: "1px solid #ccc",
    padding: 10,
    marginBottom: 10,
    backgroundColor: "#f9f9f9",
  },
  inputBox: { display: "flex" },
  input: { flex: 1, padding: 10, fontSize: 16 },
  button: { padding: "10px 20px", fontSize: 16 },
  userMsg: { textAlign: "right", margin: "5px 0" },
  botMsg: { textAlign: "left", margin: "5px 0", color: "#333" },
};

export default ChatWindow;
